package training_case_Study;

import java.util.Scanner;
import java.util.*;
public class Main {
	
	static int n, a=3;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);
		
		n=s.nextInt();
		for(int i=0; i<n; i++){
			a=a*3;
			System.out.println((a) + "");

		}

	}

}
